const input = $input.first().json;

// Fallback for subject if it's nested
let subject = input.subject || 'No Subject';
if (typeof subject === 'object' && subject?.value) {
  subject = subject.value;
}

// Extract sender info
let from = input.from?.text || input.from || '';
let senderEmail = input.from?.value?.[0]?.address || '';
let senderName = input.from?.value?.[0]?.name || '';

// Extract recipient
let to = input.to?.text || input.to || '';

// Extract and clean body
let body = input.text || input.html || '';
body = body.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();

// Final output object
return {
  emailId: input.id,
  threadId: input.threadId,
  subject,
  from,
  senderEmail,
  senderName,
  to,
  date: input.date || '',
  messageId: input.messageId || '',
  body: body.slice(0, 4000),
  snippet: input.snippet || '',
  timestamp: new Date().toISOString(),
  labels: input.labelIds || [],
};

