const aiResponse = $json.message.content;

if (!aiResponse) {
  throw new Error("No response from OpenAI");
}

let analysis;

try {
  const cleanResponse = aiResponse.replace(/```json\n?|```/g, '').trim();
  analysis = JSON.parse(cleanResponse);

  // ‚úÖ If it's an array, take the first object
  if (Array.isArray(analysis)) {
    analysis = analysis[0];
  }

  // ‚úÖ Fallbacks for missing fields
  if (!analysis.importance) analysis.importance = 'medium';
  if (!analysis.category) analysis.category = 'general';
  if (!analysis.urgency) analysis.urgency = 'normal';
  if (analysis.requiresResponse === undefined) analysis.requiresResponse = true;
  if (!analysis.sentiment) analysis.sentiment = 'neutral';
  if (!analysis.suggestedAction) analysis.suggestedAction = 'reply';
  if (!analysis.confidence) analysis.confidence = 0.7;
  if (!analysis.keyTopics) analysis.keyTopics = [];
  if (analysis.isAutomated === undefined) analysis.isAutomated = false;

} catch (error) {
  console.log('JSON parsing failed:', error.message);
  console.log('Raw response:', aiResponse);

  analysis = {
    importance: 'medium',
    category: 'general',
    urgency: 'normal',
    requiresResponse: true,
    sentiment: 'neutral',
    intent: 'Unable to parse AI response',
    suggestedAction: 'reply',
    confidence: 0.5,
    keyTopics: [],
    isAutomated: false,
    parseError: true
  };
}

const originalEmail = $node["Function - Extract Email Data"].json;

return {
  ...analysis,
  originalEmail,
  aiResponseRaw: aiResponse,
  processedAt: new Date().toISOString()
};

// Process the AI-generated response and add metadata
const aiResponse = $json.message.content;
const emailData = $('Function - Parse AI Analysis').item.json.originalEmail;
const analysis = $("Function - Parse AI Analysis").item.json;
const profile = $('Function - Load Personal Profile').item.json.profile;

// Clean and format the response
let cleanResponse = aiResponse.trim();

// Add signature if not present
if (!cleanResponse.includes(profile.communicationStyle.signatureStyle)) {
  cleanResponse += `\n\n${profile.communicationStyle.signatureStyle},\n${profile.name}`;
}

// Calculate response metrics
const responseMetrics = {
  wordCount: cleanResponse.split(' ').length,
  characterCount: cleanResponse.length,
  estimatedReadTime: Math.ceil(cleanResponse.split(' ').length / 200), // minutes
  responseLength: cleanResponse.length < 150 ? 'short' : cleanResponse.length < 300 ? 'medium' : 'long'
};

// Determine if approval is needed based on rules
const requiresApproval = 
  analysis.importance === 'high' || 
  analysis.urgency === 'urgent' ||
  analysis.confidence < 0.7 ||
  emailData.senderEmail.includes('@competitor.com') || // Add your competitor domains
  cleanResponse.length > profile.responseRules.maxAutoResponseLength;

return {
  generatedResponse: cleanResponse,
  originalEmail: emailData,
  emailAnalysis: analysis,
  responseMetadata: {
    generatedAt: new Date().toISOString(),
    model: 'gpt-4',
    confidence: analysis.confidence,
    requiresApproval,
    approvalReason: requiresApproval ? 
      (analysis.importance === 'high' ? 'High importance' :
       analysis.urgency === 'urgent' ? 'Urgent priority' :
       analysis.confidence < 0.7 ? 'Low confidence' :
       'Response too long') : null,
    metrics: responseMetrics,
    category: analysis.category,
    autoSendEligible: !requiresApproval
  },
  profileUsed: {
    name: profile.name,
    role: profile.role,
    responseStyle: profile.communicationStyle.style
  }
};

// Analyze calendar and find available meeting slots
const events = $json.items || [];
const profile = $node["Function - Load Personal Profile"].json.profile;
const emailAnalysis = $node["Function - Parse AI Analysis"].json;

// Configuration from profile
const workStart = 9; // 9 AM
const workEnd = 18; // 6 PM
const lunchStart = 12; // 12 PM
const lunchEnd = 13; // 1 PM
const meetingDuration = 30; // 30 minutes default
const bufferTime = 15; // 15 minutes buffer

// Generate available slots for next 14 days
const availableSlots = [];
const now = new Date();

for (let day = 0; day < 14; day++) {
  const date = new Date(now);
  date.setDate(date.getDate() + day);

  // Skip weekends
  const dayOfWeek = date.getDay();
  if (dayOfWeek === 0 || dayOfWeek === 6) continue;

  // Skip if it's today but already past work hours
  if (day === 0 && now.getHours() >= 17) continue;

  // Check each 30-minute slot during work hours
  for (let hour = workStart; hour < workEnd; hour++) {
    for (let minute = 0; minute < 60; minute += 30) {
      // Skip lunch hour
      if (hour >= lunchStart && hour < lunchEnd) continue;

      const slotStart = new Date(date);
      slotStart.setHours(hour, minute, 0, 0);

      // Skip past time slots for today
      if (day === 0 && slotStart <= now) continue;

      const slotEnd = new Date(slotStart);
      slotEnd.setMinutes(slotEnd.getMinutes() + meetingDuration);

      // Check for conflicts with existing events
      const hasConflict = events.some(event => {
        if (!event.start || !event.end) return false;

        const eventStart = new Date(event.start.dateTime || event.start.date);
        const eventEnd = new Date(event.end.dateTime || event.end.date);

        // Check for overlap (including buffer time)
        const bufferStart = new Date(slotStart);
        bufferStart.setMinutes(bufferStart.getMinutes() - bufferTime);
        const bufferEnd = new Date(slotEnd);
        bufferEnd.setMinutes(bufferEnd.getMinutes() + bufferTime);

        return (bufferStart < eventEnd && bufferEnd > eventStart);
      });

      if (!hasConflict) {
        const dayName = slotStart.toLocaleDateString('en-US', { weekday: 'long' });
        const timeString = slotStart.toLocaleTimeString('en-US', { 
          hour: 'numeric', 
          minute: '2-digit',
          hour12: true 
        });

        availableSlots.push({
          start: slotStart.toISOString(),
          end: slotEnd.toISOString(),
          formatted: `${dayName}, ${slotStart.toLocaleDateString()} at ${timeString}`,
          dayOfWeek: dayName,
          date: slotStart.toLocaleDateString(),
          time: timeString,
          timestamp: slotStart.getTime()
        });
      }
    }
  }
}

// Sort by timestamp and limit to top 5 slots
availableSlots.sort((a, b) => a.timestamp - b.timestamp);
const topSlots = availableSlots.slice(0, 5);

// Generate calendar summary
const calendarSummary = {
  totalEvents: events.length,
  upcomingEvents: events.filter(e => {
    const eventStart = new Date(e.start?.dateTime || e.start?.date);
    return eventStart > now;
  }).length,
  availableSlots: topSlots.length,
  nextAvailable: topSlots[0] || null
};

return {
  availableSlots: topSlots,
  calendarEvents: events,
  calendarSummary,
  originalAnalysis: emailAnalysis,
  profileData: profile,
  processedAt: new Date().toISOString()
};

// Create conversation context tracking
const emailData = $('Function - Parse AI Analysis').item.json.originalEmail;
const analysis = {
  category: $json.category,
  importance: $json.importance,
  urgency: $json.urgency,
  sentiment: $json.sentiment,
  requiresResponse: $json.requiresResponse,
  confidence: $json.confidence,
  keyTopics: $json.keyTopics || []
};
const response = $('Function - Response Processing').item.json.generatedResponse;

// Extract conversation context
const threadId = emailData.threadId;
const senderEmail = emailData.senderEmail;

// Determine conversation stage
let conversationStage = 'initial';
if (emailData.subject && emailData.subject.toLowerCase().includes('re:')) {
  conversationStage = 'ongoing';
}
if (
  response &&
  response.toLowerCase().includes('thank you') &&
  response.toLowerCase().includes('let me know')
) {
  conversationStage = 'closing';
}


// Extract key topics and entities
const keyTopics = $('Function - Parse AI Analysis').item.json.keyTopics || [];
const bodyLower = emailData.body.toLowerCase();

// Add context-specific topics
if (bodyLower.includes('project')) keyTopics.push('project');
if (bodyLower.includes('meeting')) keyTopics.push('meeting');
if (bodyLower.includes('deadline')) keyTopics.push('deadline');
if (bodyLower.includes('budget')) keyTopics.push('budget');
if (bodyLower.includes('proposal')) keyTopics.push('proposal');

// Generate follow-up recommendations
const followUpRecommendations = [];
if (analysis.category === 'meeting' && !response.includes('calendar')) {
  followUpRecommendations.push('Send calendar invite once time is confirmed');
}
if (analysis.urgency === 'urgent' && !$('IF - Requires Human Approval').item.json.responseMetadata.requiresApproval) {
  followUpRecommendations.push('Monitor for response within 4 hours');
}
if (analysis.importance === 'high') {
  followUpRecommendations.push('Follow up if no response within 24 hours');
}

// Calculate engagement score
let engagementScore = 0.5; // base score
if (analysis.sentiment === 'positive') engagementScore += 0.2;
if (analysis.importance === 'high') engagementScore += 0.2;
if (analysis.category === 'meeting') engagementScore += 0.1;
if (keyTopics.length > 2) engagementScore += 0.1;
engagementScore = Math.min(1.0, engagementScore);

const conversationContext = {
  threadId,
  senderEmail,
  senderName: emailData.senderName,
  conversationStage,
  keyTopics: [...new Set(keyTopics)], // Remove duplicates
  lastInteractionAt: new Date().toISOString(),
  responseGenerated: true,
  engagementScore,
  followUpRecommendations,
  conversationMetrics: {
    emailCount: 1, // This would be tracked across conversations
    avgResponseTime: '< 5 minutes', // Auto-response
    lastResponseMethod: 'automated',
    topicsDiscussed: keyTopics.length
  },
  nextSuggestedActions: [
    analysis.category === 'meeting' ? 'calendar_management' : 'monitor_response',
    analysis.urgency === 'urgent' ? 'priority_follow_up' : 'standard_follow_up'
  ]
};

return {
  conversationContext,
  originalData: {
    email: emailData,
    analysis,
    response: response.substring(0, 100) + '...' // Truncated for storage
  },
  trackingId: `${threadId}_${Date.now()}`,
  createdAt: new Date().toISOString()
};
